-- Create "perm_groups" table
CREATE TABLE "perm_groups" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "code" character varying NOT NULL, "name" character varying NOT NULL, PRIMARY KEY ("id"));
-- Create index "perm_groups_code_key" to table: "perm_groups"
CREATE UNIQUE INDEX "perm_groups_code_key" ON "perm_groups" ("code");
-- Create "perms" table
CREATE TABLE "perms" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "code" character varying NOT NULL, "name" character varying NOT NULL, "description" character varying NULL, "perm_group" bigint NULL, PRIMARY KEY ("id"), CONSTRAINT "perms_perm_groups_group" FOREIGN KEY ("perm_group") REFERENCES "perm_groups" ("id") ON UPDATE NO ACTION ON DELETE SET NULL);
-- Create index "perms_code_key" to table: "perms"
CREATE UNIQUE INDEX "perms_code_key" ON "perms" ("code");
-- Create "roles" table
CREATE TABLE "roles" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "code" character varying NOT NULL, "name" character varying NOT NULL, "color" character varying NULL, "description" character varying NULL, PRIMARY KEY ("id"));
-- Create index "roles_code_key" to table: "roles"
CREATE UNIQUE INDEX "roles_code_key" ON "roles" ("code");
-- Create "role_perms" table
CREATE TABLE "role_perms" ("role_id" bigint NOT NULL, "perm_id" bigint NOT NULL, PRIMARY KEY ("role_id", "perm_id"), CONSTRAINT "role_perms_perm_id" FOREIGN KEY ("perm_id") REFERENCES "perms" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "role_perms_role_id" FOREIGN KEY ("role_id") REFERENCES "roles" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
